function getWords(pattern){
	pattern = pattern.toLowerCase();

	let output = [];
    OUTER: for(let i in words) {
		let element = words[i];
		
		if(pattern.length == element.length){
			for(let ci in pattern)
				if(pattern[ci] != "_" && pattern[ci] != element[ci])
					continue OUTER;
					
			//MATCH
			output.push(element);
		}
    }
	return output;
}


function sendGuess(word){
	$("#inputChat").val(word);
	$("#formChat").submit();
}

let currentQuery = "NONE";
let candidates;

let update = function(){
	let query = $("#currentWord").text().toLowerCase();
	console.log("Query: " + query);
	
	if(query.indexOf("_") == -1){
		console.log("Now in Drawing mode");
		return;
	}
	
	if(currentQuery != query){
		console.log("Query changed");
		currentQuery = query;
		
		candidates = getWords(query);
	}
	
	console.log("Candidates: " + candidates.length);
	if(candidates.length != 0 && candidates.length < 120){
		let choosenIndex = Math.round(candidates.length * Math.random());
		let choosenWord = candidates[choosenIndex];
		candidates.splice(choosenIndex, 1);
		
		console.log("Sending: " + choosenWord);
		sendGuess(choosenWord);
	}
};

setInterval(update, 1000 * 1.2);